@Composable
fun LoginScreen(
 uiState: UiState,
 uiEffect: Flow<UiEffect>,
 onAction: (UiAction) -> Unit,
 navActions: LoginNavActions
) {
 val snackbarHostState = remember { SnackbarHostState() }
 val coroutineScope = rememberCoroutineScope()

 ABBaseScreen(
 modifier = Modifier.fillMaxSize(),
 isLoading = uiState.isLoading,
 uiEffect = uiEffect,
 snackbarHostState = snackbarHostState,
 collectEffect = { effect ->
 when (effect) {
 is UiEffect.NavigateToHome -> navActions.navigateToHome()
 is UiEffect.NavigateToSignUp -> navActions.navigateToSignUp()
 is UiEffect.NavigateToForgotPassword -> navActions.navigateToForgotPassword()
 is UiEffect.ShowError -> {
 coroutineScope.launch {
 snackbarHostState.showABSnackbar(
 message = effect.message,
 type = ABSnackbarType.ERROR
 )
 }
 }
 is UiEffect.ShowSuccess -> {
 coroutineScope.launch {
 snackbarHostState.showABSnackbar(
 message = effect.message,
 type = ABSnackbarType.SUCCESS
 )
 }
 }
 }
 },
 containerColor = MaterialTheme.colorScheme.background
 ) { paddingValues ->
 LoginContent(
 modifier = Modifier
 .fillMaxSize()
 .padding(paddingValues),
 uiState = uiState,
 onAction = onAction
 )
 }
}

@Composable
fun LoginContent(
 modifier: Modifier = Modifier,
 uiState: UiState,
 onAction: (UiAction) -> Unit
) {
 Column(
 modifier = modifier
 .verticalScroll(rememberScrollState())
 .padding(24.dp),
 horizontalAlignment = Alignment.CenterHorizontally
 ) {
 Spacer(modifier = Modifier.height(60.dp))

 // App Logo & Title
 LogoSection()

 Spacer(modifier = Modifier.height(48.dp))

 // Login Form
 LoginForm(
 uiState = uiState,
 onAction = onAction
 )

 Spacer(modifier = Modifier.height(24.dp))

 // Login Button
 ABButton(
 text = "Giriş Yap",
 onClick = { onAction(UiAction.OnLoginClicked) },
 modifier = Modifier.fillMaxWidth(),
 variant = ABButtonVariant.PRIMARY,
 size = ABButtonSize.LARGE,
 enabled = uiState.isFormValid && !uiState.isLoading,
 loading = uiState.isLoading,
 fullWidth = true
 )

 Spacer(modifier = Modifier.height(16.dp))

 // Forgot Password
 ABButton(
 text = "Şifremi Unuttum",
 onClick = { onAction(UiAction.OnForgotPasswordClicked) },
 variant = ABButtonVariant.TEXT,
 size = ABButtonSize.MEDIUM
 )

 Spacer(modifier = Modifier.height(32.dp))

 // Divider with text
 ABDividerWithText(text = "VEYA")

 Spacer(modifier = Modifier.height(32.dp))

 // Sign Up Section
 SignUpSection(onAction = onAction)

 Spacer(modifier = Modifier.height(32.dp))
 }
}

@Composable
private fun LogoSection() {
 Column(
 horizontalAlignment = Alignment.CenterHorizontally,
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 // App Icon/Logo
 Box(
 modifier = Modifier.size(80.dp),
 contentAlignment = Alignment.Center
 ) {
 Image(
 painter = painterResource(id = R.drawable.ic_launcher_foreground),
 contentDescription = "App Logo",
 modifier = Modifier.size(80.dp),
 colorFilter = ColorFilter.tint(MaterialTheme.colorScheme.primary)
 )
 }

 // App Title
 Text(
 text = "Memory Game",
 style = MaterialTheme.typography.headlineMedium.copy(
 fontWeight = FontWeight.Bold,
 color = MaterialTheme.colorScheme.onBackground
 )
 )

 // Subtitle
 Text(
 text = "Hafızan için eğlenceli meydan okuma",
 style = MaterialTheme.typography.bodyMedium.copy(
 color = MaterialTheme.colorScheme.onSurfaceVariant
 ),
 textAlign = TextAlign.Center
 )
 }
}

@Composable
private fun LoginForm(
 uiState: UiState,
 onAction: (UiAction) -> Unit
) {
 Column(
 verticalArrangement = Arrangement.spacedBy(16.dp)
 ) {
 // Email Field
 ABTextField(
 value = uiState.email,
 onValueChange = { onAction(UiAction.OnEmailChanged(it)) },
 label = "E-posta",
 placeholder = "ornek@email.com",
 leadingIcon = Icons.Default.Email,
 isError = uiState.emailError != null,
 errorMessage = uiState.emailError,
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Email
 ),
 modifier = Modifier.fillMaxWidth()
 )

 // Password Field
 ABTextField(
 value = uiState.password,
 onValueChange = { onAction(UiAction.OnPasswordChanged(it)) },
 label = "Şifre",
 placeholder = "Şifrenizi girin",
 leadingIcon = Icons.Default.Lock,
 trailingIcon = null,
 onTrailingIconClick = null,
 visualTransformation = if (uiState.isPasswordVisible) {
 VisualTransformation.None
 } else {
 PasswordVisualTransformation()
 },
 isError = uiState.passwordError != null,
 errorMessage = uiState.passwordError,
 keyboardOptions = KeyboardOptions(
 keyboardType = KeyboardType.Password
 ),
 modifier = Modifier.fillMaxWidth()
 )
 }
}

