 )
 )

 ABButton(
 text = "Hesap Oluştur",
 onClick = { onAction(UiAction.OnSignUpClicked) },
 variant = ABButtonVariant.TEXT,
 size = ABButtonSize.MEDIUM
 )
 }
}

