package com.oguzhanozgokce.androidbootcampfinalproject.common.exception

/**
 * Base exception class for all custom exceptions in the application.
 * Provides common properties and functionality for all exceptions.
 */
sealed class BaseException(
 override val message: String,
 override val cause: Throwable? = null
) : Exception(message, cause) {

 /**
 * Whether this exception should be logged as an error.
 * Some exceptions like validation errors might not need full error logging.
 */
 open val shouldLog: Boolean = true
}

enum class ErrorSeverity {
 LOW,
 MEDIUM,
 HIGH,
 CRITICAL
}

