```kotlin:app/src/main/java/com/oguzhanozgokce/androidbootcampfinalproject/common/exception/ExceptionMapper.kt, lines=ALL(1-90)
package com.oguzhanozgokce.androidbootcampfinalproject.common.exception

import com.google.firebase.FirebaseException
import com.google.firebase.FirebaseNetworkException
import com.google.firebase.FirebaseTooManyRequestsException
import com.google.firebase.auth.FirebaseAuthException
import com.google.firebase.firestore.FirebaseFirestoreException
import com.oguzhanozgokce.androidbootcampfinalproject.common.FirebaseErrorCode
import kotlinx.coroutines.TimeoutCancellationException
import java.io.IOException
import java.net.UnknownHostException

/**
 * Simplified exception mapper that converts any exception to AppException.
 */
object ExceptionMapper {

 fun mapException(exception: Throwable): AppException {
 return when (exception) {
 // Already our exception - pass through
 is AppException -> exception

 // Firebase Auth Exceptions
 is FirebaseAuthException -> AppException(
 code = exception.errorCode,
 cause = exception
 )

 // Firebase Firestore Exceptions
 is FirebaseFirestoreException -> AppException(
 code = mapFirestoreCode(exception.code),
 cause = exception
 )

 // Firebase Network Exceptions
 is FirebaseNetworkException -> AppException(
 errorCode = FirebaseErrorCode.NETWORK_ERROR,
 cause = exception
 )

 is FirebaseTooManyRequestsException -> AppException(
 errorCode = FirebaseErrorCode.QUOTA_EXCEEDED,
 cause = exception
 )

 // Generic Network Exceptions
 is UnknownHostException -> AppException(
 errorCode = FirebaseErrorCode.NETWORK_ERROR,
 cause = exception
 )

 is IOException -> AppException(
 errorCode = FirebaseErrorCode.NETWORK_ERROR,
 cause = exception
 )

 // Timeout Exceptions
 is TimeoutCancellationException -> AppException(
 errorCode = FirebaseErrorCode.TIMEOUT,
 cause = exception
 )

 // Generic Exceptions
 else -> AppException(
 errorCode = FirebaseErrorCode.UNKNOWN,
 message = exception.message ?: "Bilinmeyen hata",
 cause = exception
 )
 }
 }

 private fun mapFirestoreCode(code: FirebaseFirestoreException.Code): String {
 return when (code) {
 FirebaseFirestoreException.Code.PERMISSION_DENIED -> "permission-denied"
 FirebaseFirestoreException.Code.NOT_FOUND -> "not-found"
 FirebaseFirestoreException.Code.ALREADY_EXISTS -> "already-exists"
 FirebaseFirestoreException.Code.RESOURCE_EXHAUSTED -> "resource-exhausted"
 FirebaseFirestoreException.Code.UNAUTHENTICATED -> "unauthenticated"
 FirebaseFirestoreException.Code.UNAVAILABLE -> "unavailable"
 FirebaseFirestoreException.Code.DEADLINE_EXCEEDED -> "deadline-exceeded"
 FirebaseFirestoreException.Code.DATA_LOSS -> "data-loss"
 FirebaseFirestoreException.Code.INVALID_ARGUMENT -> "invalid-argument"
 FirebaseFirestoreException.Code.CANCELLED -> "cancelled"
 FirebaseFirestoreException.Code.FAILED_PRECONDITION -> "failed-precondition"
 FirebaseFirestoreException.Code.OUT_OF_RANGE -> "out-of-range"
 else -> "unknown"
 }
 }
}

```
