```kotlin:app/src/main/java/com/oguzhanozgokce/androidbootcampfinalproject/common/exception/ErrorHandler.kt, lines=ALL(1-42)
package com.oguzhanozgokce.androidbootcampfinalproject.common.exception

import android.util.Log

/**
 * Centralized error handling and logging system.
 * This handles logging, analytics, and user notification for exceptions.
 */
object ErrorHandler {

 private const val TAG = "ErrorHandler"

 /**
 * Handles an exception by logging it and optionally sending to analytics.
 * Returns a user-friendly message that can be shown to the user.
 */
 fun handleException(exception: BaseException): String {
 when (exception.severity) {
 ErrorSeverity.LOW -> {
 if (exception.shouldLog) {
 Log.d(TAG, "Low severity error: ${exception.message}", exception)
 }
 }
 ErrorSeverity.MEDIUM -> {
 Log.w(TAG, "Medium severity error: ${exception.message}", exception)
 }
 ErrorSeverity.HIGH -> {
 Log.e(TAG, "High severity error: ${exception.message}", exception)
 }
 ErrorSeverity.CRITICAL -> {
 Log.e(TAG, "CRITICAL ERROR: ${exception.message}", exception)
 }
 }

 if (exception.context.isNotEmpty()) {
 Log.d(TAG, "Error context: ${exception.context}")
 }

 return exception.userMessage
 }
}

