 .whereEqualTo("userId", userId)
 .orderBy("score", Query.Direction.DESCENDING)
 .orderBy("timestamp", Query.Direction.DESCENDING)
 .get()
 .await()

 snapshot.documents.mapNotNull { doc ->
 doc.toObject(GameScoreDto::class.java)?.copy(id = doc.id)
 }.toDomainList()
 }

