package com.oguzhanozgokce.androidbootcampfinalproject.domain.usecase

import android.util.Patterns
import com.oguzhanozgokce.androidbootcampfinalproject.common.FirebaseCustomException
import com.oguzhanozgokce.androidbootcampfinalproject.domain.model.User
import com.oguzhanozgokce.androidbootcampfinalproject.domain.repository.AuthRepository
import javax.inject.Inject

class SignUpUseCase @Inject constructor(
 private val authRepository: AuthRepository
) {
 suspend operator fun invoke(
 email: String,
 password: String,
 displayName: String
 ): Result<User> {
 if (email.isBlank()) {
 return Result.failure(FirebaseCustomException("E-posta adresi boş olamaz", "missing-email"))
 }

 if (password.isBlank()) {
 return Result.failure(FirebaseCustomException("Şifre boş olamaz", "missing-password"))
 }

 if (displayName.isBlank()) {
 return Result.failure(FirebaseCustomException("İsim boş olamaz", "missing-display-name"))
 }

 if (!Patterns.EMAIL_ADDRESS.matcher(email).matches()) {
 return Result.failure(FirebaseCustomException("Geçersiz e-posta adresi", "invalid-email"))
 }

 if (password.length < 6) {
 return Result.failure(FirebaseCustomException("Şifre en az 6 karakter olmalı", "weak-password"))
 }

 return authRepository.signUpWithEmailAndPassword(email, password, displayName)
 }
}
```
